elgg.provide("hj.framework.tabs");hj.framework.tabs.init=function(){$(".hj-ajax-tab-load > a").unbind(".ajaxTabLoad").bind("click.ajaxTabLoad",hj.framework.tabs.load);$("li.elgg-state-selected > a").trigger("click.ajaxTabLoad")};hj.framework.tabs.load=function(a){var b=$(this).attr("href"),c=$(this).data("options"),d="#"+c.params.target;var e='<div class="hj-ajax-loader hj-loader-circle"></div>';$(d).show().html(e);$(this).parent().siblings("li").removeClass("elgg-state-selected");$(this).parent().addClass("elgg-state-selected");elgg.action(b,{data:c,success:function(a){$(d).html(a.output.data);elgg.trigger_hook("success","hj:framework:ajax")}});a.preventDefault()};elgg.register_hook_handler("init","system",hj.framework.tabs.init);